window.searchModulesForKeyword = (keyword, matchCase = false) => {
  return Webpack.getAllModules(module => {
    const props = [module, Object.getPrototypeOf(module)]
      .map(Object.keys).reduce((prev, curr) => prev.concat(curr));

    return props.some(prop => {
      if (!matchCase) prop = prop.toLowerCase();
      if (~prop.indexOf(keyword)) return true;
    })
  });
};

