Webpack VSearch:
window.searchModulesForValue = (keyword, matchCase = false) => {
  return Webpack.getAllModules(module => {
    return Object.values(module).some(prop => {
      if (typeof prop === 'string' || prop instanceof String) {
        if (!matchCase) prop = prop.toLowerCase();
        if (~prop.indexOf(keyword)) return true;
      }
    });
  });
};

return () => delete window.searchModulesByValue;

